import React, { useState } from 'react';

const RegisterModal = ({ open, onClose, onRegistered }) => {
    const [formData, setFormData] = useState({
        email: '',
        username: '',
        password: '',
        confirmPassword: '',
        firstName: '',
        lastName: '',
        phone: '',
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [formStep, setFormStep] = useState(1); // Для многошаговой формы
    const [acceptedTerms, setAcceptedTerms] = useState(false);

    if (!open) return null;

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
        if (error) setError('');
    };

    const validateStep1 = () => {
        const { email, username, password, confirmPassword } = formData;

        if (!email || !username || !password || !confirmPassword) {
            setError('Пожалуйста, заполните все обязательные поля');
            return false;
        }

        if (!email.match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/)) {
            setError('Введите корректный email адрес');
            return false;
        }

        if (password.length < 6) {
            setError('Пароль должен содержать минимум 6 символов');
            return false;
        }

        if (password !== confirmPassword) {
            setError('Пароли не совпадают');
            return false;
        }

        if (username.length < 3) {
            setError('Имя пользователя должно содержать минимум 3 символа');
            return false;
        }

        return true;
    };

    const handleNextStep = () => {
        if (validateStep1()) {
            setFormStep(2);
            setError('');
        }
    };

    const handlePrevStep = () => {
        setFormStep(1);
        setError('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        if (!acceptedTerms) {
            setError('Необходимо принять условия пользовательского соглашения');
            return;
        }

        if (!validateStep1()) {
            setFormStep(1);
            return;
        }

        setLoading(true);
        try {
            // Симуляция API вызова
            await new Promise(resolve => setTimeout(resolve, 1500));

            // Анимация успеха
            document.querySelector('.register-success').classList.remove('hidden');
            await new Promise(resolve => setTimeout(resolve, 1000));

            onRegistered({
                email: formData.email,
                password: formData.password,
            });

            onClose();
        } catch (err) {
            setError(err.message || 'Ошибка регистрации');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="fixed inset-0 z-50 overflow-y-auto">
            {/* Backdrop с размытием */}
            <div className="fixed inset-0 bg-gradient-to-br from-gray-900/80 via-blue-900/50 to-purple-900/60 backdrop-blur-sm" onClick={onClose}></div>

            {/* Success animation - скрыто по умолчанию */}
            <div className="register-success hidden fixed inset-0 flex items-center justify-center z-60">
                <div className="relative">
                    <div className="absolute inset-0 bg-gradient-to-r from-emerald-500 to-green-500 rounded-full animate-ping opacity-20"></div>
                    <div className="relative w-24 h-24 bg-gradient-to-r from-emerald-500 to-green-500 rounded-full flex items-center justify-center shadow-2xl">
                        <svg className="w-12 h-12 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M5 13l4 4L19 7" />
                        </svg>
                    </div>
                </div>
            </div>

            <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
                <div className="inline-block align-bottom bg-white rounded-2xl text-left overflow-hidden shadow-2xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
                    {/* Gradient Header */}
                    <div className="relative bg-gradient-to-r from-blue-500 via-purple-600 to-pink-500 p-6">
                        <div className="absolute top-0 left-0 right-0 h-1 bg-gradient-to-r from-yellow-400 via-orange-500 to-red-500"></div>
                        <div className="flex items-center justify-between">
                            <div>
                                <h3 className="text-2xl font-bold text-white font-['Poppins']">Создать аккаунт</h3>
                                <p className="text-blue-100 text-sm mt-1 font-['Inter']">
                                    Присоединяйтесь к ZMarket сегодня
                                </p>
                            </div>
                            <button
                                onClick={onClose}
                                className="text-white/80 hover:text-white bg-white/10 hover:bg-white/20 rounded-full p-2 transition-all duration-300"
                            >
                                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                                </svg>
                            </button>
                        </div>

                        {/* Progress Steps */}
                        <div className="flex items-center justify-center mt-6">
                            <div className="flex items-center">
                                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${formStep === 1 ? 'bg-white text-blue-600' : 'bg-white/20 text-white'}`}>
                                    1
                                </div>
                                <div className={`w-16 h-1 mx-2 ${formStep >= 2 ? 'bg-white' : 'bg-white/30'}`}></div>
                                <div className={`w-8 h-8 rounded-full flex items-center justify-center ${formStep === 2 ? 'bg-white text-blue-600' : 'bg-white/20 text-white'}`}>
                                    2
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* Form Content */}
                    <div className="p-6 bg-gradient-to-b from-white to-gray-50">
                        {error && (
                            <div className="mb-4 p-3 rounded-xl bg-gradient-to-r from-red-50 to-pink-50 border border-red-200 animate-shake">
                                <div className="flex items-center">
                                    <div className="w-6 h-6 bg-gradient-to-r from-red-500 to-pink-500 rounded-full flex items-center justify-center mr-3">
                                        <svg className="w-3 h-3 text-white" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={3} d="M6 18L18 6M6 6l12 12" />
                                        </svg>
                                    </div>
                                    <span className="text-red-700 font-medium text-sm">{error}</span>
                                </div>
                            </div>
                        )}

                        <form onSubmit={handleSubmit}>
                            {formStep === 1 ? (
                                <div className="space-y-4">
                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2 font-['Inter']">
                                            <span className="flex items-center">
                                                <svg className="w-4 h-4 text-blue-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 12a4 4 0 10-8 0 4 4 0 008 0zm0 0v1.5a2.5 2.5 0 005 0V12a9 9 0 10-9 9m4.5-1.206a8.959 8.959 0 01-4.5 1.207" />
                                                </svg>
                                                Email адрес
                                            </span>
                                        </label>
                                        <input
                                            type="email"
                                            name="email"
                                            value={formData.email}
                                            onChange={handleChange}
                                            required
                                            className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all duration-300 font-['Inter']"
                                            placeholder="your@email.com"
                                        />
                                    </div>

                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2 font-['Inter']">
                                            <span className="flex items-center">
                                                <svg className="w-4 h-4 text-purple-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z" />
                                                </svg>
                                                Имя пользователя
                                            </span>
                                        </label>
                                        <input
                                            type="text"
                                            name="username"
                                            value={formData.username}
                                            onChange={handleChange}
                                            required
                                            className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-purple-500 focus:ring-2 focus:ring-purple-200 transition-all duration-300 font-['Inter']"
                                            placeholder="Придумайте никнейм"
                                        />
                                    </div>

                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2 font-['Inter']">Пароль</label>
                                            <input
                                                type="password"
                                                name="password"
                                                value={formData.password}
                                                onChange={handleChange}
                                                required
                                                className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all duration-300 font-['Inter']"
                                                placeholder="Минимум 6 символов"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2 font-['Inter']">Подтвердите пароль</label>
                                            <input
                                                type="password"
                                                name="confirmPassword"
                                                value={formData.confirmPassword}
                                                onChange={handleChange}
                                                required
                                                className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all duration-300 font-['Inter']"
                                                placeholder="Повторите пароль"
                                            />
                                        </div>
                                    </div>

                                    <div className="pt-4">
                                        <button
                                            type="button"
                                            onClick={handleNextStep}
                                            className="w-full px-6 py-3 rounded-xl font-medium text-white transition-all duration-300 group overflow-hidden bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 active:scale-95 shadow-lg hover:shadow-xl font-['Inter']"
                                        >
                                            <span className="relative z-10 flex items-center justify-center space-x-2">
                                                <span>Далее</span>
                                                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M14 5l7 7m0 0l-7 7m7-7H3" />
                                                </svg>
                                            </span>
                                        </button>
                                    </div>
                                </div>
                            ) : (
                                <div className="space-y-4">
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2 font-['Inter']">Имя</label>
                                            <input
                                                type="text"
                                                name="firstName"
                                                value={formData.firstName}
                                                onChange={handleChange}
                                                className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-blue-500 focus:ring-2 focus:ring-blue-200 transition-all duration-300 font-['Inter']"
                                                placeholder="Ваше имя"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-2 font-['Inter']">Фамилия</label>
                                            <input
                                                type="text"
                                                name="lastName"
                                                value={formData.lastName}
                                                onChange={handleChange}
                                                className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-purple-500 focus:ring-2 focus:ring-purple-200 transition-all duration-300 font-['Inter']"
                                                placeholder="Ваша фамилия"
                                            />
                                        </div>
                                    </div>

                                    <div>
                                        <label className="block text-sm font-medium text-gray-700 mb-2 font-['Inter']">
                                            <span className="flex items-center">
                                                <svg className="w-4 h-4 text-green-500 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                                                </svg>
                                                Телефон (необязательно)
                                            </span>
                                        </label>
                                        <input
                                            type="tel"
                                            name="phone"
                                            value={formData.phone}
                                            onChange={handleChange}
                                            className="w-full px-4 py-3 border-2 border-gray-200 rounded-xl focus:outline-none focus:border-green-500 focus:ring-2 focus:ring-green-200 transition-all duration-300 font-['Inter']"
                                            placeholder="+7 (999) 999-99-99"
                                        />
                                    </div>

                                    <div className="pt-4">
                                        <div className="flex items-start mb-6">
                                            <input
                                                type="checkbox"
                                                id="terms"
                                                checked={acceptedTerms}
                                                onChange={(e) => setAcceptedTerms(e.target.checked)}
                                                className="mt-1 mr-3 w-5 h-5 text-blue-600 bg-gray-100 border-gray-300 rounded focus:ring-blue-500 focus:ring-2"
                                            />
                                            <label htmlFor="terms" className="text-sm text-gray-600">
                                                Я принимаю{' '}
                                                <a href="/terms" className="text-blue-600 hover:text-blue-800 font-medium">
                                                    Условия использования
                                                </a>{' '}
                                                и{' '}
                                                <a href="/privacy" className="text-blue-600 hover:text-blue-800 font-medium">
                                                    Политику конфиденциальности
                                                </a>
                                            </label>
                                        </div>

                                        <div className="flex gap-3">
                                            <button
                                                type="button"
                                                onClick={handlePrevStep}
                                                className="flex-1 px-6 py-3 rounded-xl font-medium transition-all duration-300 group overflow-hidden bg-gradient-to-r from-gray-100 to-gray-200 hover:from-gray-200 hover:to-gray-300 active:scale-95 shadow-sm font-['Inter']"
                                            >
                                                <span className="relative z-10 flex items-center justify-center space-x-2 text-gray-700">
                                                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 19l-7-7m0 0l7-7m-7 7h18" />
                                                    </svg>
                                                    <span>Назад</span>
                                                </span>
                                            </button>
                                            <button
                                                type="submit"
                                                disabled={loading || !acceptedTerms}
                                                className="flex-1 px-6 py-3 rounded-xl font-medium text-white transition-all duration-300 group overflow-hidden bg-gradient-to-r from-blue-500 to-purple-600 hover:from-blue-600 hover:to-purple-700 active:scale-95 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed font-['Inter']"
                                            >
                                                <span className="relative z-10 flex items-center justify-center space-x-2">
                                                    {loading ? (
                                                        <>
                                                            <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                                                            <span>Регистрация...</span>
                                                        </>
                                                    ) : (
                                                        <>
                                                            <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M18 9v3m0 0v3m0-3h3m-3 0h-3m-2-5a4 4 0 11-8 0 4 4 0 018 0zM3 20a6 6 0 0112 0v1H3v-1z" />
                                                            </svg>
                                                            <span>Зарегистрироваться</span>
                                                        </>
                                                    )}
                                                </span>
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            )}

                            <div className="mt-6 pt-6 border-t border-gray-200">
                                <p className="text-center text-sm text-gray-500 font-['Inter']">
                                    Уже есть аккаунт?{' '}
                                    <button
                                        type="button"
                                        className="text-blue-600 hover:text-blue-800 font-medium"
                                        onClick={() => {
                                            onClose();
                                            // Здесь можно вызвать функцию открытия формы входа
                                        }}
                                    >
                                        Войти
                                    </button>
                                </p>
                            </div>
                        </form>
                    </div>
                </div>
            </div>

            {/* CSS для анимаций */}
            <style jsx>{`
                @keyframes shake {
                    0%, 100% { transform: translateX(0); }
                    10%, 30%, 50%, 70%, 90% { transform: translateX(-5px); }
                    20%, 40%, 60%, 80% { transform: translateX(5px); }
                }
                .animate-shake {
                    animation: shake 0.5s ease-in-out;
                }
            `}</style>
        </div>
    );
};

export default RegisterModal;
