import React, { useState } from 'react';
import Filters from './Filters';
import ProductsGrid from './ProductsGrid';
import Pagination from './Pagination';

const Marketplace = ({ token, onRequireAuth }) => {
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState('id');
    const [currentPage, setCurrentPage] = useState(0);
    const [showProductModal, setShowProductModal] = useState(false);

    // Тестовые данные для отображения
    const testProducts = [
        {
            id: 1,
            productUUID: '1',
            title: 'Смартфон iPhone 15 Pro Max',
            description: 'Новый флагманский смартфон с улучшенной камерой и процессором A17 Pro',
            price: 129990,
            stock: 15,
            rating: 4.8,
            ratingCount: 142,
            mainAttachmentId: 'test1'
        },
        {
            id: 2,
            productUUID: '2',
            title: 'Ноутбук MacBook Air M2',
            description: 'Легкий и мощный ноутбук с чипом Apple M2, 13-дюймовый дисплей Retina',
            price: 149990,
            stock: 8,
            rating: 4.7,
            ratingCount: 89,
            mainAttachmentId: 'test2'
        },
        {
            id: 3,
            productUUID: '3',
            title: 'Наушники Sony WH-1000XM5',
            description: 'Беспроводные наушники с активным шумоподавлением премиум-класса',
            price: 34990,
            stock: 25,
            rating: 4.9,
            ratingCount: 203,
            mainAttachmentId: 'test3'
        },
        {
            id: 4,
            productUUID: '4',
            title: 'Умные часы Apple Watch Series 9',
            description: 'Умные часы с функцией измерения ЭКГ и пульсоксиметром',
            price: 45990,
            stock: 12,
            rating: 4.6,
            ratingCount: 67,
            mainAttachmentId: 'test4'
        },
        {
            id: 5,
            productUUID: '5',
            title: 'Планшет iPad Air 5',
            description: 'Мощный планшет с чипом M1 и поддержкой Apple Pencil 2',
            price: 79990,
            stock: 7,
            rating: 4.5,
            ratingCount: 45,
            mainAttachmentId: 'test5'
        },
        {
            id: 6,
            productUUID: '6',
            title: 'Игровая консоль PlayStation 5',
            description: 'Новейшая игровая консоль с поддержкой 4K и ray tracing',
            price: 69990,
            stock: 3,
            rating: 4.8,
            ratingCount: 156,
            mainAttachmentId: 'test6'
        },
        {
            id: 7,
            productUUID: '7',
            title: 'Фотоаппарат Sony A7 IV',
            description: 'Беззеркальная камера с полнокадровым сенсором 33 Мп',
            price: 219990,
            stock: 5,
            rating: 4.9,
            ratingCount: 78,
            mainAttachmentId: 'test7'
        },
        {
            id: 8,
            productUUID: '8',
            title: 'Электросамокат Xiaomi Pro 2',
            description: 'Электросамокат с запасом хода 45 км и максимальной скоростью 25 км/ч',
            price: 32990,
            stock: 18,
            rating: 4.4,
            ratingCount: 92,
            mainAttachmentId: 'test8'
        }
    ];

    const pageSize = 8;
    const totalPages = Math.ceil(testProducts.length / pageSize);

    const getDisplayedProducts = () => {
        let filtered = [...testProducts];

        if (searchTerm.trim()) {
            filtered = filtered.filter(
                (p) =>
                    p.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                    p.description.toLowerCase().includes(searchTerm.toLowerCase())
            );
        }

        filtered.sort((a, b) => {
            switch (sortBy) {
                case 'price':
                    return a.price - b.price;
                case 'rating':
                    return (b.rating || 0) - (a.rating || 0);
                case 'title':
                    return a.title.localeCompare(b.title);
                default:
                    return (a.id || 0) - (b.id || 0);
            }
        });

        const startIndex = currentPage * pageSize;
        const endIndex = startIndex + pageSize;
        return filtered.slice(startIndex, endIndex);
    };

    const displayedProducts = getDisplayedProducts();

    return (
        <div className="min-h-screen bg-gradient-to-b from-white to-blue-50">
            <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
                {/* Заголовок */}
                <div className="mb-8 text-center">
                    <h1 className="text-4xl font-bold text-gray-900 mb-2">
                        Добро пожаловать в <span className="text-blue-600">ZMarket</span>
                    </h1>
                    <p className="text-gray-600 max-w-2xl mx-auto">
                        Лучший выбор электроники, гаджетов и современных технологий.
                        Находите то, что нужно, по выгодным ценам.
                    </p>
                </div>

                {/* Фильтры */}
                <Filters
                    searchTerm={searchTerm}
                    setSearchTerm={setSearchTerm}
                    sortBy={sortBy}
                    setSortBy={setSortBy}
                />

                {/* Статистика */}
                <div className="mb-6 flex flex-wrap items-center justify-between">
                    <div>
                        <p className="text-gray-600">
                            Найдено товаров: <span className="font-semibold">{testProducts.length}</span>
                        </p>
                    </div>
                    {token && (
                        <button
                            onClick={() => setShowProductModal(true)}
                            className="px-4 py-2 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-lg hover:from-blue-600 hover:to-purple-700 transition-all shadow-md"
                        >
                            + Добавить товар
                        </button>
                    )}
                </div>

                {/* Сетка товаров */}
                <ProductsGrid
                    products={displayedProducts}
                    canManage={false}
                    showBuy={true}
                    onRequireAuth={onRequireAuth}
                />

                {/* Пагинация */}
                {totalPages > 1 && (
                    <Pagination
                        currentPage={currentPage}
                        totalPages={totalPages}
                        onPageChange={setCurrentPage}
                    />
                )}

                {/* Сообщение если товаров нет */}
                {displayedProducts.length === 0 && (
                    <div className="text-center py-16">
                        <div className="w-24 h-24 mx-auto mb-6 text-gray-300">
                            <svg fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1.5}
                                    d="M20 7l-8-4-8 4m16 0l-8 4m8-4v10l-8 4m0-10L4 7m8 4v10M4 7v10l8 4" />
                            </svg>
                        </div>
                        <h3 className="text-xl font-semibold text-gray-900 mb-2">
                            Товары не найдены
                        </h3>
                        <p className="text-gray-600 mb-4">
                            Попробуйте изменить параметры поиска или загрузите товары позже.
                        </p>
                        <button
                            onClick={() => {
                                setSearchTerm('');
                                setCurrentPage(0);
                            }}
                            className="px-6 py-3 bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-lg hover:from-blue-600 hover:to-blue-700 transition-all shadow-md"
                        >
                            Сбросить фильтры
                        </button>
                    </div>
                )}

                {/* Информационный блок */}
                <div className="mt-12 p-6 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl border border-blue-100">
                    <h3 className="text-xl font-bold text-gray-900 mb-3">Почему выбирают ZMarket?</h3>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                        <div className="flex items-start space-x-3">
                            <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                                <span className="text-blue-600 font-bold">✓</span>
                            </div>
                            <div>
                                <h4 className="font-semibold text-gray-900">Гарантия качества</h4>
                                <p className="text-gray-600 text-sm">Все товары проверены и имеют гарантию</p>
                            </div>
                        </div>
                        <div className="flex items-start space-x-3">
                            <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                                <span className="text-blue-600 font-bold">🚚</span>
                            </div>
                            <div>
                                <h4 className="font-semibold text-gray-900">Быстрая доставка</h4>
                                <p className="text-gray-600 text-sm">Доставка по всей России за 1-3 дня</p>
                            </div>
                        </div>
                        <div className="flex items-start space-x-3">
                            <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
                                <span className="text-blue-600 font-bold">👑</span>
                            </div>
                            <div>
                                <h4 className="font-semibold text-gray-900">Лучшие цены</h4>
                                <p className="text-gray-600 text-sm">Цены ниже рыночных на 5-15%</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Marketplace;
