import React, { useRef, useEffect } from 'react';

const GradientWaveCanvas = () => {
    const canvasRef = useRef(null);
    const animationRef = useRef(null);
    const timeRef = useRef(0);

    useEffect(() => {
        const canvas = canvasRef.current;
        if (!canvas) return;

        const ctx = canvas.getContext('2d');
        let width = window.innerWidth;
        let height = window.innerHeight;

        const resizeCanvas = () => {
            width = window.innerWidth;
            height = window.innerHeight;
            canvas.width = width;
            canvas.height = height;
        };

        const drawWave = (amplitude, frequency, speed, color, yOffset, alpha = 0.1) => {
            ctx.beginPath();
            ctx.moveTo(0, height / 2 + yOffset);

            for (let x = 0; x <= width; x += 10) {
                const y = amplitude * Math.sin(x * frequency + timeRef.current * speed) +
                          amplitude * 0.3 * Math.sin(x * frequency * 1.7 + timeRef.current * speed * 0.7) +
                          height / 2 + yOffset;
                ctx.lineTo(x, y);
            }

            ctx.lineTo(width, height);
            ctx.lineTo(0, height);
            ctx.closePath();

            const gradient = ctx.createLinearGradient(0, 0, width, 0);
            gradient.addColorStop(0, `${color}00`);
            gradient.addColorStop(0.3, `${color}${Math.floor(alpha * 255).toString(16).padStart(2, '0')}`);
            gradient.addColorStop(0.7, `${color}${Math.floor(alpha * 255).toString(16).padStart(2, '0')}`);
            gradient.addColorStop(1, `${color}00`);

            ctx.fillStyle = gradient;
            ctx.fill();
        };

        const animate = () => {
            ctx.clearRect(0, 0, width, height);

            // Фоновый градиент
            const bgGradient = ctx.createLinearGradient(0, 0, 0, height);
            bgGradient.addColorStop(0, '#f8fcff');
            bgGradient.addColorStop(1, '#f0f9ff');
            ctx.fillStyle = bgGradient;
            ctx.fillRect(0, 0, width, height);

            // Несколько слоев волн с разными параметрами
            drawWave(20, 0.005, 0.02, '#a8d8ff', 100, 0.08);  // Голубой
            drawWave(25, 0.003, 0.015, '#b8c6ff', 50, 0.06);  // Сиреневый
            drawWave(30, 0.002, 0.01, '#c8d4ff', 0, 0.04);    // Лавандовый
            drawWave(15, 0.008, 0.03, '#a0c8f0', 150, 0.05);  // Светло-синий

            // Тонкие линии волн
            ctx.beginPath();
            for (let i = 0; i < 3; i++) {
                ctx.moveTo(0, height / 2 + 80);
                for (let x = 0; x <= width; x += 5) {
                    const y = 10 * Math.sin(x * 0.004 + timeRef.current * 0.02 + i) + height / 2 + 80;
                    ctx.lineTo(x, y);
                }
                ctx.strokeStyle = `rgba(168, 216, 255, 0.05)`;
                ctx.lineWidth = 0.5;
                ctx.stroke();
            }

            timeRef.current += 0.01;
            animationRef.current = requestAnimationFrame(animate);
        };

        resizeCanvas();
        window.addEventListener('resize', resizeCanvas);
        animate();

        return () => {
            cancelAnimationFrame(animationRef.current);
            window.removeEventListener('resize', resizeCanvas);
        };
    }, []);

    return (
        <canvas
            ref={canvasRef}
            className="fixed top-0 left-0 w-full h-full -z-10 pointer-events-none"
            style={{ opacity: 0.6 }}
        />
    );
};

export default GradientWaveCanvas;
